import { AnyOptionsSchema, InferredSchema } from "../options";
import { Input } from "../types/inputs";
export type InputDefinition<Result, OptionsSchema extends AnyOptionsSchema | undefined = undefined> = OptionsSchema extends object ? InputDefinitionWithOptions<Result, OptionsSchema> : InputDefinitionWithoutOptions<Result>;
export interface InputDefinitionWithoutOptions<Result> {
    produce: Input<Result>;
}
export interface InputDefinitionWithOptions<Result, OptionsSchema extends AnyOptionsSchema | undefined> {
    options: OptionsSchema;
    produce: Input<Result, InferredSchema<OptionsSchema>>;
}
export declare function createInput<Result, OptionsSchema extends AnyOptionsSchema | undefined = undefined>(definition: InputDefinition<Result, OptionsSchema>): Input<Result, InferredSchema<OptionsSchema>>;
